		Shell shell = UIThreadRunnable.syncExec(new Result<Shell>() {
			@Override
			public Shell run() {
				IWorkbenchWindow window = PlatformUI.getWorkbench().getActiveWorkbenchWindow();
				return (window == null) ? null : window.getShell();
